package LeetCodeProblems;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

class LC2266_CountNumberOfTextsTest {
    private LC2266_CountNumberOfTexts solution;

    @BeforeEach
    void setUp() {
        solution = new LC2266_CountNumberOfTexts();
    }

    @Test
    void countTexts1() {
        assertEquals(960, solution.countTexts("344644887777599999"));
    }

    @Test
    void countTexts2() {
        assertEquals(8, solution.countTexts("344644885"));
    }

    @Test
    void countTexts3() {
        assertEquals(8, solution.countTexts("22233"));
    }

    @Test
    void countTexts4() {
        assertEquals(82876089, solution.countTexts("222222222222222222222222222222222222"));
    }

    @Test
    void countTexts5() {
        assertEquals(537551452, solution.countTexts("444444444444444444444444444444448888888888888888999999999999333333333333333366666666666666662222222222222222666666666666666633333333333333338888888888888888222222222222222244444444444444448888888888888222222222222222288888888888889999999999999999333333333444444664"));
    }

    @Test
    void countTexts6() {
        assertEquals(886136986, solution.countTexts(""));
    }
}